mapper.select:
select
  o_id, u_id, ts_id, ot_id, o_tile, o_state, o_image, o_describe, o_end_time, o_create_time
from order


        and o_id = #{oId}


        and u_id = #{uId}


        and ts_id = #{tsId}


        and ot_id = #{otId}


        and o_tile = #{oTile}


        and o_state = #{oState}


        and o_image = #{oImage}


        and o_describe = #{oDescribe}


        and o_end_time = #{oEndTime}


        and o_create_time = #{oCreateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from order


        and o_id = #{oId}


        and u_id = #{uId}


        and ts_id = #{tsId}


        and ot_id = #{otId}


        and o_tile = #{oTile}


        and o_state = #{oState}


        and o_image = #{oImage}


        and o_describe = #{oDescribe}


        and o_end_time = #{oEndTime}


        and o_create_time = #{oCreateTime}
